-- Create premium_purchases table
create table if not exists public.premium_purchases (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references auth.users(id) on delete cascade not null,
  stripe_payment_intent_id text not null,
  amount numeric not null,
  currency text not null default 'eur',
  status text not null,
  created_at timestamptz default timezone('utc'::text, now()) not null,
  updated_at timestamptz default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.premium_purchases enable row level security;

-- Create RLS policies
create policy "Users can view their own purchases"
  on public.premium_purchases
  for select
  using (auth.uid() = user_id);

create policy "Users can insert their own purchases"
  on public.premium_purchases
  for insert
  with check (auth.uid() = user_id);

-- Add premium status to profiles table
alter table public.profiles add column if not exists is_premium boolean default false;
alter table public.profiles add column if not exists premium_expires_at timestamptz;

-- Create index for faster queries
create index if not exists premium_purchases_user_id_idx on public.premium_purchases(user_id);
create index if not exists profiles_is_premium_idx on public.profiles(is_premium);

